public class SingleThreadRender {    Boolean textDone = false;    Boolean downloadDone = false;    public SingleThreadRender() { }    void renderPage() {        Thread text = new TextThread();        Thread download = new DownloadThread();        Thread image = new ImageThread();        text.start();        download.start();        image.start();    }    class TextThread extends Thread {        public void run() {            renderText();            textDone = true;            try {                textDone.notify();            } catch (Exception ex) { }        }    }    class DownloadThread extends Thread {        public void run() {            scanForImageInfo();            downloadImages();            downloadDone = true;            try {                downloadDone.notify();            } catch (Exception ex) { }        }    }    class ImageThread extends Thread {        public void run() {            while (!textDone || !downloadDone) {                try {                    textDone.wait();                    downloadDone.wait();                } catch (Exception ex) { }            }            renderImages();        }    }    static void renderText() {        for (int i = 0; i < 200; ++i) {            System.out.println("Rendering text");        }    }    static void scanForImageInfo() {        for (int i = 0; i < 300; ++i) {            System.out.println("Scanning images");        }    }    static void downloadImages() {        for (int i = 0; i < 200; ++i) {            System.out.println("Downloading images");        }    }    static void renderImages() {        for (int i = 0; i < 200; ++i) {            System.out.println("Rendering images");        }    }    public static void main(String[] args) {        SingleThreadRender r = new SingleThreadRender();        r.renderPage();    }}